mapper.insert:
INSERT INTO BM_LIVING_LOG(
	  MNG_ID
	, SEND_DATE
	, ATTR_ID
)
VALUES(
	  #{MNG_ID}
	, NOW()
	, (SELECT DL_CD FROM SM_DL_CD_INFO WHERE CO_CD = 'LIVING_RESULT' AND NUM_VAL4 = #{ATTR_ID})
)

INSERT INTO BM_DVC_LOC_LOG(
	  MNG_ID
	, SEND_DATE
	, LATI
	, LONGI
	, SPD
	, HEADING
	, EPLY_ID
	, ROUT_ID
	, REMARK
)
VALUES(
	  #{DEVICE_ID}


			, DATE_FORMAT(#{GPS_DT}, '%Y-%m-%d %H:%i:%s')


			, NOW()




			, #{LATITUDE}


			, NULL




			, #{LONGITUDE}


			, NULL


	, #{SPEED}
	, #{HEADING}
	, #{EMPLOYEE_ID}
	, #{ROUTE_ID}
	, #{REMARK}
)

INSERT INTO BM_DVC_COND_LOG(
	  MNG_ID
	, SEND_DATE
	, DVC_COND
	, VER_INFO
	, REMARK
)
VALUES

(
	  #{item.deviceId}
	, NOW()
	, (SELECT DL_CD FROM SM_DL_CD_INFO WHERE CO_CD = 'DVC_COND' AND NUM_VAL4 = #{item.statusCode})
	, #{item.version}
	, #{item.REMARK}
)

mapper.update:
UPDATE ${TABLE_NAME}
SET
	  RSV_ID		= #{RSV_ID}
	, MNG_ID		= #{MNG_ID}
	, PROCE_RST		= (SELECT DL_CD FROM SM_DL_CD_INFO WHERE CO_CD = 'FTP_RESULT' AND NUM_VAL4 = #{RESULT_CODE})
	, SEND_DATE		= NOW()
	, COMPLETE_YN	= 'Y'
WHERE
	RSV_ID = #{RSV_ID}
	AND MNG_ID = #{MNG_ID}
