mapper.update:
    INSERT INTO IMP_IT_VERSION(
    	IMP_ID
    	, PRCS_NAME
    	, PRCS_INDEX
    	, VER_HIGH
    	, VER_MID
    	, VER_LOW
    	, START_DATE
    	, LAST_DATE
    	, BUILD_DATE
    	, UPDATE_DATE)

    VALUES(
    	  #{IMP_ID}
    	, #{PRCS_NAME}
    	, #{PRCS_INDEX}
    	, #{VER_HIGH}
    	, #{VER_MID}
    	, #{VER_LOW}
    	, #{START_DATE}
    	, #{LAST_DATE}
    	, #{BUILD_DATE}
    	, #{UPDATE_DATE}
    )

	ON DUPLICATE KEY UPDATE
	PRCS_INDEX = #{PRCS_INDEX}
	, VER_HIGH = #{VER_HIGH}
	, VER_MID = #{VER_MID}
	, VER_LOW = #{VER_LOW}
	, START_DATE = #{START_DATE}
	, LAST_DATE = #{LAST_DATE}
	, BUILD_DATE = #{BUILD_DATE}
	, UPDATE_DATE = #{UPDATE_DATE}

mapper.select:
SELECT T1.IMP_ID AS impId, T1.PRCS_NAME AS processName, T1.PRCS_INDEX AS processIndex, T2.IMP_NAME AS impName, T1.VER_HIGH AS versionHigh, T1.VER_MID AS versionMiddle, T1.VER_LOW AS versionLow, T1.START_DATE AS startTime, T1.LAST_DATE AS lastRunTime, T1.BUILD_DATE AS buildDate, T1.UPDATE_DATE AS updateDate FROM IMP_IT_VERSION T1 INNER JOIN IMP_MT_TERMINAL T2 ON T1.IMP_ID = T2.IMP_ID
WHERE T1.IMP_ID = #{IMP_ID}
ORDER BY T1.IMP_ID, T1.PRCS_INDEX
